class Solution(object):
    def isSameTree(self, p, q):
        """
        :type p: TreeNode
        :type q: TreeNode
        :rtype: bool
        """

        # inOrder traversal 
        # base case
        if not p and not q: 
            return True 

        # if one tree has next level and the other doesn't -> return false
        if not p or not q: 
            return False

        if p.val != q.val:
            return False 

        # check left subtree and check right subtree
        return self.isSameTree(p.left, q.left) and self.isSameTree(p.right, q.right)

