                    ++count_of_islands;
                    std::queue<pair<int, int>> bfs_que;//<row, column>
                    grid[row][col] = '2';
                    bfs_que.push(make_pair(row, col));
                    while(!bfs_que.empty()) {
                        std::pair<int, int> visited_node = bfs_que.front();
                        bfs_que.pop();
                        for(auto d : dir) {
                            int next_row = visited_node.first + d.first;
