                to_visit.popleft()

                # mark current node 
                grid[curri][currj] = '0'

                adj_dims = [(curri + 1, currj), (curri - 1, currj), \
                    (curri, currj + 1), (curri, currj-1)]
                filtered = []

                # make sure not out of bounds
                for x, y in adj_dims:
                    m, n = len(grid), len(grid[0])
                    if x < m and x >= 0 and y < n and y >= 0:
                        filtered.append((x, y))

                for x, y in filtered:
                    if grid[x][y] == '1':
                        to_visit.append((x, y))

        soln = 0
        for row in range(m):
            for col in range(n):
                if grid[row][col] == '1':
                    # see an island
                    soln += 1 
                    # mark it 
                    bfs(row, col, grid)

        return soln





