class Solution {
    public int numIslands(char[][] grid) {
        // no empty grid edge case - m and n are >= 1

        int islands = 0;

        // each island is equal to completing a BFS search every time you find a "1" in the grid
        // to make sure we only consider that island once, we turn each visited "1" node into a "0"
        for (int i=0; i<grid.length; i++) {
            for (int j=0; j<grid[0].length; j++) {
                if (grid[i][j] == '1') {
                    makeIsland(grid, i, j);
                    islands++;
                }
            }
        }
        return islands;
    }

    // make an island at starting position (i,j) by using BFS to explore all neighbours
    private static void makeIsland(char[][] grid, int i, int j) {
        Queue<Pair<Integer, Integer>> q = new LinkedList<>();
        q.add(new Pair(i, j));

        while (!q.isEmpty()) {
            Pair<Integer, Integer> p = q.poll();
            int pi = p.getKey(); int pj = p.getValue();
            grid[pi][pj] = '0';

            List<Pair<Integer, Integer>> neighbours = neighbours(grid.length, grid[0].length, pi, pj);
            for (Pair<Integer, Integer> n : neighbours) {
                int ni = n.getKey();
                int nj = n.getValue();
                if (grid[ni][nj] == '1') {
                    q.add(new Pair(ni, nj));
                }
            }
        }
    }

    // get all neighbours for any (i,j) in a (m x n) grid
    private static List<Pair<Integer, Integer>> neighbours(int m, int n, int i, int j) {
        List<Pair<Integer, Integer>> neighbours = new ArrayList<>();

        // top, right, bottom, left respectively
        int[] di = {-1, 0, 1, 0};
        int[] dj = {0, 1, 0, -1};
        for (int k=0; k<di.length; k++) {
            int newI = i + di[k];
            int newJ = j + dj[k];
            if (newI >= 0 && newI < m && newJ >= 0 && newJ < n) {
                neighbours.add(new Pair<>(newI, newJ));
            }
        }
        return neighbours;
    }
}
