class Solution {
    public void findSecretWord(String[] wordlist, Master master) {
        List<String> list = new ArrayList<>(Arrays.asList(wordlist));

        // we can only guess 10 times.
        for(int i = 0; i < 10; i++) {
            int index = new Random().nextInt(list.size());
            String currentWord = list.get(index);

            int guessMatchCount = master.guess(currentWord);

            // if the guess count matches the length of current word, we found it.
            if(guessMatchCount == currentWord.length()) return;

            // remove current.
            list.remove(index);

            // find all words that isn't math count of the currentWord guess
            int j = 0;
