class Solution {
    // function to Compare the Words, and
	// maintain the counter of occurance of matches
  private:
    int checkString(string & word1, string & word2) {
      int counter = 0;

      for (int i = 0; i < 6; i++) {
        if (word1[i] == word2[i]) {
          counter++;
        }
      }
      return counter;
    }

