class Solution {
public:
    void findSecretWord(vector<string>& words, Master& master) {
        while (!words.empty()) {
            int i = rand() % words.size();
            string cur_word = words[i];
            int num = master.guess(cur_word);
            if (num == 6) {
                //cout << "The word! ";
                break;
            }
            else {
                swap(words[i], words.back());
                words.pop_back();
                check_words(words, cur_word, num);
            }
        }
    }

private:
    void check_words (vector<string>& words, string& cur_word, int num) {
        int cnt_words = words.size();
        int i = 0;
        while (cnt_words--) {
            int cnt_letters = 0;
            for (int j = 0; j < 6; j++) {
                if (words[i][j] == cur_word[j]) {
                    cnt_letters++;
                }
            }

            if (cnt_letters != num) {
                swap(words[i], words.back());
                words.pop_back();
            } else {
                i++;
            }
        }
    }
};
