```
   class Solution {
public:

    const bool USE_OCCURANCE = true;

    void findSecretWord(vector<string>& wordlist, Master& master) {

        vector<bool> wordgone(wordlist.size(), false);     // keep track of words we have ruled out

        int guesses = 10;
        while (guesses) {

            vector<unordered_set<int>> occur(26);       // word occurance for each character

            for (int i = 0; i < wordlist.size(); i++) {
                if (!wordgone[i]) {
                    for (char ch : wordlist[i]) {
                        occur[ch-'a'].insert(i);  
                    }
                }
            }

            sort(occur.begin(), occur.end(), [](auto& a, auto& b){
                return a.size() > b.size();     // highest occurance first
            });

            int idx;
            if (USE_OCCURANCE) {      
                idx = *(occur[0].begin());      // pick one of the words with the highest occurance
            }
            else {
                // find the first word that has not been ruled out
                idx = 0;
                while (wordgone[idx]) {
                    idx++;
                }
            }
            string& cur = wordlist[idx];

            int g = master.guess(cur);
            guesses--;

            if (g == 6) {
                cout << "used " << 10-guesses << " guesses" << endl;
                return;
