            for (int i = 0; i < wordlist.size(); i++) {

                if (!wordgone[i]) {

                    // check for same number of matches
                    int m = 0;
                    for (int j = 0; j < 6; j++) {
                        if (cur[j] == wordlist[i][j]) {
                            m++;
                        }
                    }
                    if (m != g) {
                        wordgone[i] = true;
                    }
                }   
            }
        }
    }
};
