class Solution {

    public void findSecretWord(String[] wordlist, Master master) {
        //1. First guess any word randomly!
        String initialWord = getInitialWord(wordlist);

        //2. Add it to candidates list!
        List<String> candidates = new ArrayList<>();
        candidates.add(initialWord);
        String bestCandidate = initialWord;

        // 3. Loop as long as we have candidates!
        while (!candidates.isEmpty()) {

            // 3.1. Guess and find a score!
            int score = master.guess(bestCandidate);

            if (score == 6) {
                System.out.println("Matching is found!");
                return;
            }

            // 3.2. Get all candidates that matches the score by calculating the hamming distance.
            candidates = getCandidates(wordlist, bestCandidate, score);

            // 3.3. Get the best candidate based on the one with the most commonality!
            bestCandidate = getBestCandidate(candidates);

            // 3.4. Update word list with new candidates
            wordlist = (String[]) candidates.toArray(new String[candidates.size()]);
        } 

        System.out.println("[ERROR] No matching is found!");
    }

    private String getBestCandidate(List<String> candidates) {
    	int[][] commonalityArray = new int[26][10];

    	for (String candidate : candidates) {
    		char[] candidateChars = candidate.toCharArray();

    		int pos = 0;
    		for (char candidateChar : candidateChars) {
    			int row = candidateChar - 'a';
    			int col = pos;

    			commonalityArray[row][col] = commonalityArray[row][col] + 1;
    			++pos;
    		}

    	}

    	int maxScore = -1;
    	String bestCandidate = "";

    	for (int i = 0; i < candidates.size(); ++i) {
    		String candidate = candidates.get(i);
    		char[] candidateChars = candidate.toCharArray();
    		int current = 0;
    		int pos = 0;

    		for (char candidateChar : candidateChars) {
    			current += commonalityArray[candidateChar - 'a'][pos];
    			++pos;
    		}

    		if (current > maxScore) {
    			maxScore = current;
    			bestCandidate = candidate;
    		}
    	}

    	return bestCandidate;
	}

	String getInitialWord(String[] wordlist) {
        Random random = new Random();   
        int selectedWordIndex = random.nextInt(wordlist.length);

        return wordlist[selectedWordIndex];
    }

    List<String> getCandidates(String[] wordlist, String bestCandidate, int score) {
    	List<String> out = new ArrayList<>();

    	for (String word : wordlist) {
    		int distance = calcDistance(word, bestCandidate);

    		if (distance == score) {
    			out.add(word);
    		}
    	}

    	return out;
    }

    int calcDistance(String word1, String word2) {
    	char[] word1Chars = word1.toCharArray();
    	char[] word2Chars = word2.toCharArray();
    	int count = 0;

    	for (int i = 0; i < word1Chars.length; ++i) {
    		if (word1Chars[i] == word2Chars[i]) {
    			++count;
    		}
    	}

    	return count;
    }

}
