var findSecretWord = function(wordlist, master) {
    let updatedList = wordlist;
    const match2Words = (word1, word2) => {
        let counter = 0;
        for(let g=0 ;g<6; g++){
            if(word1[g] == word2[g]) counter++;
        }

        return counter;

    }

    const removedRedundantData = (matchValue, word) => {
       let compressedList = []; 
       for(let curWord of updatedList){
           if(curWord == word) continue; // no need to have same word again;
           let wordsMatch = match2Words(word, curWord);
           if( wordsMatch >= matchValue ) compressedList.push(curWord)
       }

      if(compressedList.length)
          updatedList = compressedList;

    }

    const searchInWordlist = (index) => {
        let match = master.guess(updatedList[index]);
        if(match == 6) return true;
        if(match > 0) removedRedundantData(match, updatedList[index]);
        return false;
    }



    const getIndexToSearch = () => {
        return Math.floor(Math.random() * updatedList.length);
    }

    for(let g=0; g<10; g++){ // As we can hit 10 iterations
        let res = searchInWordlist(getIndexToSearch());
        if(res) return;
    }

    return -1;
};
