class Solution {
    public void findSecretWord(String[] wordlist, Master master) {
        TreeSet<String> words = new TreeSet<>(Arrays.asList(wordlist));

        while (!words.isEmpty()) {
            String word = words.pollFirst();

            // get the response back from api call
            int resp = master.guess(word);

            if (resp == 6) return;

            words.removeIf(wordInSet -> evaluate(wordInSet, word) != resp);
        }

        return;
    }

    // this method just counts how many chars at the same position two strings have in common
    private int evaluate(String s, String t) {
        int count = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == t.charAt(i)) count++;
        }
        return count;
    }
}

