```
class Solution {
    static public int countMatching(final String a, final String b) {
        int matches = 0;
        for (int i = 0; i < a.length(); ++i) {
            if (a.charAt(i) == b.charAt(i)) {
                ++matches;
            }
        }
        return matches;
    }

    static void println(final String str) {
        System.out.println(str);
    }

    int[][] matchingChars;

    public void findSecretWord(String[] wordlist, Master master) {
        // Build up a 2 dimensional matrix with the number of matching characters between the
        // two words indexed by i and j.
        int N = wordlist.length;
        matchingChars = new int[N][N];
        for (int i = 0; i < N; ++i)
            for (int j = i; j < N; ++j) {
                matchingChars[i][j] = matchingChars[j][i] = countMatching(wordlist[i], wordlist[j]);
            }

        // Initialize the possible words list to have indexes for the full word list
        List<Integer> possible = new ArrayList();
        Set<Integer> impossible = new HashSet<>();
        for (int i = 0; i < N; ++i) {
            possible.add(i);
        }

        while (!possible.isEmpty()) {
            int guess = chooseNextGuess(possible, impossible);
            int matches = master.guess(wordlist[guess]);
            // println("Guessed " + wordlist[guess] + "; " + matches + " characters matched.");
            if (matches == wordlist[0].length()) {
                return;
            }

            // Narrow down the possible list to words that have the same number of characters matching
            // with the last guess as the last guess has matches to the correct answer.
            List<Integer> possible2 = new ArrayList();
            for (Integer j: possible) {
                if (matchingChars[guess][j] == matches) {
                    possible2.add(j);
                }
                possible = possible2;
            }

            impossible.add(guess);
        }

    }

    public int chooseNextGuess(List<Integer> possible, Set<Integer> impossible) {
        // println("" + possible.size() + " possible words remain.");

        if (possible.size() <= 2) {
            return possible.get(0);
        }
        List<Integer> bestGroup = possible;
        int bestGuess = -1;

        for (int guess = 0; guess < matchingChars.length; ++guess) {
            if (!impossible.contains(guess)) {
                // Group all other words based on the number of characters that match
                // with guess
                Map<Integer, ArrayList<Integer>> groups = new HashMap<>();
                for (Integer j: possible)
                    if (j != guess) {
                        int matching = matchingChars[guess][j];
                        groups.compute(matching, (key, oldValue) -> {
                            if (oldValue == null) {
                                oldValue = new ArrayList<>();
                            }
                            oldValue.add(j);
                            return oldValue;
                        });
                    }

                // Find the largest group
                List<Integer> largestGroup = Collections.EMPTY_LIST;
                for (List<Integer> group: groups.values())
                    if (group.size() > largestGroup.size()) {
                        largestGroup = group;
                    }

                // Keep if this is smaller that the current best
                if (largestGroup.size() < bestGroup.size()) {
                    bestGroup = largestGroup;
                    bestGuess = guess;
                    // println("New min from a group of " + bestGroup.size() + " words.");
                }
            }
        }

        // println("Choose from a group of " + bestGroup.size() + " words.");
        return bestGuess;
    }
}
