class Solution {
    int getMatched(string left, string right) {
        int matched = 0;
        for(int i = 0; i < left.size(); i++) {
            if (left[i] == right[i]) {
                matched++;
            }
        }
        return matched;
    }

    vector<string> getNextList(vector<string>& wordlist, string current, int matched) {
        vector<string> toReturn;

        for(string str: wordlist) {
            if (getMatched(str, current) == matched) {
                toReturn.push_back(str);
            }
        }

        return toReturn;
    }
public:
    void findSecretWord(vector<string>& wordlist, Master& master) {
        int index = rand() % wordlist.size();
        int matched = master.guess(wordlist[index]);
        if (matched == wordlist.front().size()) {
            return;
        }

        auto nextlist = getNextList(wordlist, wordlist[index], matched);
        findSecretWord(nextlist, master);
    }
};
