class Solution {
public:
    bool checkStraightLine(vector<vector<int>>& coordinates) {
        
        // in this case I've taken x/y instead of slope (y/x) for comparison, though either one works
        float diff = (float)(coordinates[1][0] - coordinates[0][0]) / (coordinates[1][1] - coordinates[0][1]);

        // denominator is 0 set diff to a dummy variable as in this case or you can set it to INT_MAX
        if (coordinates[1][1] == coordinates[0][1]) diff = -100000;

        for (int i{2}; i<coordinates.size(); ++i) {

            // compare in case of line parallel to x axis [all y coordinates same]
            if (coordinates[i][1]==coordinates[i-1][1]) {
                if (diff==-100000) continue;
                else return false;
            }

            // compare the x/y values and check if they are equal or not
            if (((float)(coordinates[i][0] - coordinates[i-1][0]) / (coordinates[i][1] - coordinates[i-1][1]))==diff) continue;
            else {
                return false;
            }
        }
        return true;
    }
};
