impl Solution {
    pub fn check_straight_line(coordinates: Vec<Vec<i32>>) -> bool {
        let mut prevx = coordinates[0][0];
        let mut prevy = coordinates[0][1];
        let mut xdiff = coordinates[1][0] - prevx;
        let mut ydiff = coordinates[1][1] - prevy;
        let mut tilt = 0;
        if xdiff != 0 && ydiff != 0{
            tilt = ydiff/xdiff;
        } else if xdiff == 0 {
            tilt = 2^32;
        }
        for coord in coordinates.iter().skip(1){
            if (coord[0] - prevx) != 0 && (coord[1] - prevy) != 0{
                if ((coord[1] - prevy)/(coord[0] - prevx)) != tilt {
                    return false
                }else{
                    prevx = coord[0];
                    prevy = coord[1];
                }
            }else if (coord[0] - prevx) == 0{
                if xdiff != coord[0] - prevx{
                    return false
                }else{
                    prevx = coord[0];
                    prevy = coord[1];
                }
            }else {
                if ydiff != coord[1] - prevy{
                    return false
                }else{
                    prevx = coord[0];
                    prevy = coord[1];
                }
            }
        }
        return true
    }
}
