    // Remind that the cross product of two collinear vectors is equal to zero vector
    //
    // X  Y   Z
    // X1 Y1  Z1
    // X2 Y2  Z2
    //
    // Cross product = (Y1*Z2 - Y2*Z1, X2*Z1 - X1*Z2, X1*Y2 - X2*Y1)
    //
    // Since we are dealing with 2D vectors, Z1 = Z2 = 0
    // And this is simplified to:
    // Cross product = (0, 0, X1*Y2 - X2*Y1)
    //
    // Hence we can check the collinearity of 2 vectors this way:
    inline bool is_collinear(int x1, int y1, int x2, int y2) const {
        return (x1*y2 - x2*y1) == 0;
    }

    // Now the solution is trivial
    bool checkStraightLine(vector<vector<int>>& coordinates) {
        int x1 = coordinates[1][0] - coordinates[0][0];
        int y1 = coordinates[1][1] - coordinates[0][1];

        for (int i = 2; i < coordinates.size(); i++) {
            int x2 = coordinates[i][0] - coordinates[0][0];
            int y2 = coordinates[i][1] - coordinates[0][1];
            if (!is_collinear(x1, y1, x2, y2)) return false;
        }

        return true;
    }
