class Solution {
    public static int func(int[] nums, int n, int i, int j, int flag, int[][][] dp) {
        if(i == n) return flag == 3 ? 0 : n;
        if(dp[i][j][flag] != -1) return dp[i][j][flag];
        if(j == n) {
            int m1 = func(nums, n, i + 1, i, 1, dp);
            int m2 = 1 + func(nums, n, i + 1, j, flag, dp);
            return Math.min(m1, m2);
        }
        int min = 1 + func(nums, n, i + 1, j, flag, dp);
        if(flag == 1 && nums[i] > nums[j]) min = Math.min(min, Math.min(func(nums, n, i + 1, i, 2, dp), func(nums, n, i + 1, i, 1, dp)));
        if((flag == 3 || flag == 2) && nums[i] < nums[j]) min = Math.min(min, func(nums, n, i + 1, i, 3, dp));
        return dp[i][j][flag] = min;
    }
    public int minimumMountainRemovals(int[] nums) {
        int n = nums.length;
        int[][][] dp = new int[n + 1][n + 1][4];
        for(int i = 0; i <= n; i++) {
            for(int j = 0; j <= n; j++) {
                dp[i][j][0] = dp[i][j][1] = dp[i][j][2] = dp[i][j][3] = -1;
            }
        }
        return func(nums, n, 0, n, 0, dp);
    }
}

