    public int minimumMountainRemovals(int[] nums) {
        int n=nums.length;
        int left[]=new int[n];
        for(int i=0;i<n;i++)
        {
            left[i]=1;
            for(int j=0;j<i;j++)
            {
                if(nums[i]>nums[j])
