class Solution {
public:

    int minimumMountainRemovals(vector<int>& nums) 
    {
        int n = nums.size();
        if(n <= 3) return 0;
        vector<int> dp1(n, 1), dp2(n, 1);

        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < i; j++)
            {
                if(nums[i] > nums[j])
                    dp1[i] = max(dp1[i], dp1[j]+1);
            }
        }

        for(int i = n-1; i >= 0; i--)
        {
            for(int j = n-1; j > i; j--)
            {
                if(nums[i] > nums[j])
                    dp2[i] = max(dp2[i], dp2[j]+1);
            }
        }

        int maxi = -1e9;

        for(int i = 0; i < n; i++)
        {
            if(dp1[i] != 1 && dp2[i] != 1)
                maxi = max(maxi, dp1[i] + dp2[i] - 1);
        }

        return n - maxi;
    }
};

