class Solution {
public:
    int minimumMountainRemovals(vector<int>& nums) {
        int n = nums.size();
        vector<int> LISFront(n,1);
        vector<int> LISBack(n,1);
        for(int i = 0; i < n; i++)
        {
            for(int j = 0; j < i; j++)
            {
                if(nums[j] < nums[i] && LISFront[i] <= LISFront[j])
                    LISFront[i] = 1+ LISFront[j];
            }
        }
        for(int i = n-1; i >= 0; i--)
        {
            for(int j = n-1; j >= i; j--)
            {
                if(nums[j] < nums[i] && LISBack[i] <= LISBack[j])
                    LISBack[i] = 1+ LISBack[j];
            }
        }
        int ans = 0;
        for(int i = 0; i < n; i++)
        {
            if(LISBack[i] == 1 || LISFront[i] == 1) continue;
            ans = max(ans, LISBack[i]+LISFront[i]-1);
        }
        return n-ans;
    }
};
