        int size=nums.size();
        vector<int> lis(size,1);
        for(int i=0;i<size;i++){
            int maxYet=0;
            for(int j=0;j<i;j++){
                if(nums[j]<nums[i]){
                    maxYet=max(maxYet,lis[j]);
                }
            }
            lis[i]=maxYet+1;
        }
        vector<int> ldp(size,1);
        for(int i=size-2;i>=0;i--){
