        for(int i=0;i<nums.size();i++){
             if(nums[i]>tail[len-1]){
                tail[len]=nums[i];len++;dp[i]=len;
            }
            else{
                int x=helper(tail,0,len-1,nums[i]);
                tail[x]=nums[i];dp[i]=x+1;
