Just find the LIS from the left and the right, then just find the best index **i**. Make sure the length of 2 LIS greater or equal 2.
```
class Solution {
 public:
  int minimumMountainRemovals(vector<int>& nums) {
    int n = nums.size();
    vector<int> leftLIS(n, 0), rightLIS(n, 0);
    // Find the left LIS
    for (int i = 0; i < n; i++) {
      int bestVal = 0;
      for (int j = i - 1; j >= 0; j--) {
        if (nums[i] > nums[j] && leftLIS[j] > bestVal) {
          bestVal = leftLIS[j];
        }
      }
      leftLIS[i] = bestVal + 1;
    }
    // Find the right LIS
    for (int i = n - 1; i >= 0; i--) {
      int bestVal = 0;
      for (int j = i + 1; j < n; j++) {
        if (nums[i] > nums[j] && rightLIS[j] > bestVal) {
          bestVal = rightLIS[j];
        }
      }
      rightLIS[i] = bestVal + 1;
    }
    int result = INT_MAX;
    for (int i = 1; i < n - 1; i++) {
      result = min(result, n - (leftLIS[i] + rightLIS[i] - 1));
    }
    return result;
  }
};
