The idea is that we calculate longgest increasing subset end at the position i(inc[i]) and longgest decreasing subset start at the position dec[i] using binary search. Then we scan the array and we can find the answer.
```
class Solution {
public:
    int minimumMountainRemovals(vector<int>& nums) {
        vector<int> inc(nums.size()), dec(nums.size()), v;
        inc[0] = 1;
        dec[nums.size() - 1] = 1;
        v.push_back(nums[0]);
        for(int i = 1; i < nums.size(); i++){
            if(nums[i] > v.back()){
                v.push_back(nums[i]);
                inc[i] = v.size();
            }
            else{
                int s = 0, e = v.size() - 1;
                while(s < e){
                    int mid = (s+e)/2;
                    if(v[mid] >= nums[i]){
                        e = mid;
                    }
                    else
                        s = mid + 1;
                }
                inc[i] = s + 1;
                v[s] = nums[i];
            }
        }
        v.clear();
        v.push_back(nums.back());
        for(int i = nums.size() - 2; i > - 1; i--){
            if(nums[i] > v.back()){
                v.push_back(nums[i]);
                dec[i] = v.size();
            }
            else{
                int s = 0, e = v.size() - 1;
                while(s < e){
                    int mid = (s+e)/2;
                    if(v[mid] >= nums[i]){
                        e = mid;
                    }
                    else
                        s = mid + 1;
                }
                dec[i] = s + 1;
                v[s] = nums[i];
            }
        }
        int m = 0;
    //conside i the middle
    for(int i=1; i<nums.size()-1; i++) {
        if(inc[i]>1 && dec[i]>1) {
            int ans = inc[i]+dec[i]-1;
            m = max(m, ans);
        }
    }
        return nums.size() - m;
    }
};
