class Solution {
    public int minimumMountainRemovals(int[] nums) {

        // LIS(i) = longest increasing subsequence ending at index i

        int n = nums.length;
        int[] leftIncreasing = new int[n];
        int[] rightIncreasing = new int[n];

        // Base Cases
        leftIncreasing[0] = 1;
        rightIncreasing[n-1] = 1;

        // Computing LIS from left to right
        for(int i = 1; i < n; i++){
            leftIncreasing[i] = 1;

            for(int j = 0; j < i; j++){

                if(nums[j] < nums[i] && leftIncreasing[j] + 1 > leftIncreasing[i]){
                    leftIncreasing[i] = leftIncreasing[j] + 1;
                }
            }        
        }

        // Computing LIS from right to left

        for(int i = n - 2; i > -1; i--){
            rightIncreasing[i] = 1;

            for(int j = n - 1; j > i; j--){

                if(nums[i] > nums[j] && rightIncreasing[j] + 1 > rightIncreasing[i]){
                    rightIncreasing[i] = rightIncreasing[j] + 1;
                }
            }
        }


        //adding the length of LIS from left and right at index i and subtracting 1
        //gives  length of mountain array with its peek at i
        // we need to find the maxLength of a MountainArray

        int maxLenMountainArray = 0;


        // the first and last element can obviously not be a peek since they don't fulfill the property
        // i.e: resulting mountain would be only ascending or descending
        // one of LIS cannot be 1 since that would mean that LIS starts and ends there. i.e same as described above 

        for(int i = 1; i < n - 1; i++){
            if(!(leftIncreasing[i] == 1 || rightIncreasing[i] == 1)){
                 if(leftIncreasing[i] + rightIncreasing[i] > maxLenMountainArray){
                    maxLenMountainArray = leftIncreasing[i] + rightIncreasing[i] - 1;
                }
            }           
        }

        // the number of deletions is length of nums - length of mountain array

        return n - maxLenMountainArray;

    }
}
