class Solution:
    def minimumMountainRemovals(self, nums: List[int]) -> int:
        n = len(nums)        
        left = [0 for i in range(n)]
        right = [0 for i in range(n)]

        for i in range(1, n):
            val = 0 
            for j in range(i):
                if nums[j] < nums[i]:
                    val = max(val, left[j] + 1)

            left[i] = val

