    int minimumMountainRemovals(vector<int>& nums) {
        int n = nums.size();
        // from lest to right, longest increasing subsequence
        vector<int> up(n, 1);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < i; j++) {
                if (nums[j] < nums[i])
                    up[i] = max(up[i], up[j] + 1);
            }
        }
        // from right to left, longest increasing subsequence
        vector<int> down(n, 1);
        for (int i = n-1; i >= 0; i--) {
            for (int j = n-1; j > i; j--) {
                if (nums[i] > nums[j])
                    down[i] = max(down[i], down[j] + 1);
            }
        }

        // let's find the maximum length of bitonic sequence to get minimum removal!
        int res = 0;
        for (int i = 0; i < n; i++) 
            if (up[i] >= 2 && down[i] >= 2) // basic condition for bitonic sequence!
                res = max(res, up[i] + down[i] - 1);

        return n - res;
    }
