class Solution:
    def minimumMountainRemovals(self, nums: List[int]) -> int:

        # we cannot have all increasing or all decreasing array so we need to remove these items first

        j = 1

        # count the number of elements that are increasing at the begining
        while nums[j] <= nums[j-1]:
            j += 1

        # count the number of elements that are decreasing in reversed array
        l = len(nums) - 2        
        while nums[l] <= nums[l + 1]:
            l -= 1

        n = len(nums) - 1   # save the length of original array

        nums = nums[j - 1 : l + 2] # update  the array and remove the increasing and decreasing part

        L = len(nums) - 1


        dp = []
        ans = []
        # find the number of minimum removing items until index i for increasing array (left to right)
        for i in range(L + 1):
            ind = bisect_left(ans, nums[i])

            if ind == len(ans):
                ans.append(nums[i])
            else:
                ans[ind] = nums[i]
            dp.append(i - ind)

        ans = []
        # find the number of minimum removing item until index i for decreasing array (right to left)
        for i in reversed(range(L + 1)):
            ind = bisect_left(ans, nums[i])

            if ind == len(ans):
                ans.append(nums[i])
            else:
                ans[ind] = nums[i]
            dp[i] += L - i - ind



        return min(dp) + j - 1 + n - (l + 1) 
