class Solution {
public:
    void getlis(vector<int>v,vector<int>&req)
    {
        for(int i=0;i<v.size();i++)req.push_back(1);
        vector<int>vv;

            vv.push_back(v[0]);

       for(int i=1;i<v.size();i++)
        {

            if(v[i]<vv[0])vv[0]=v[i];
           else
           {
               if(v[i]>vv[vv.size()-1])
               {
                   vv.push_back(v[i]);
                   req[i]=vv.size();
               }
               else
               {
                   int ind=lower_bound(vv.begin(),vv.end(),v[i])-vv.begin();
                   vv[ind]=v[i];
                   req[i]=ind+1;

               }
           }
        }


    }
    int minimumMountainRemovals(vector<int>& nums) {
      vector<int>left,right;int ee=0;
        getlis(nums,left);
        reverse(nums.begin(),nums.end());
         getlis(nums,right);
        int ans=0;
      int ab=nums.size();
        for(int i=0;i<ab;i++)
        {
            if(left[i]>1&&right[ab-i-1]>1)
                ans=max(ans,left[i]+right[ab-i-1]-1);
        }
        return ab-ans;

    }
};
