class Solution {
    public void helper1(int[] nums,int c,ArrayList<Integer> it,int[] inc){
        int right = it.size()-1,left = 0;
        while(left<right){
            int mid = (left+right)/2;
            if(it.get(mid) >= nums[c])
                right = mid;
            else
                left = mid+1;
        }
        inc[c] = left+1;
        it.set(left,nums[c]);
    }
    public void lis(int[] nums,int[] inc,int n){
        ArrayList<Integer> it = new ArrayList<>();
        for(int i=0;i<n;i++){
            if(it.size() == 0){
                it.add(nums[i]);
                inc[i] = 1;
            }
            else if(it.get(0) >= nums.length){
                it.set(0,nums[i]);
                inc[i] = 1;
            }
            else if(it.get(it.size()-1) < nums[i]){
                it.add(nums[i]);
                inc[i] = it.size();
            }
            else
                helper1(nums,i,it,inc);  
        }
    }
	public int minimumMountainRemovals(int[] nums) {
		int n = nums.length;
		int[] inc = new int[n];
		int[] dec = new int[n];
		lis(nums,inc,n);
        int[] rev = new int[n];
        for(int i=0;i<n;i++)
            rev[i] = nums[n-i-1];
        int[] rd = new int[n];
        lis(rev,rd,n);
        for(int i=0;i<n;i++)
            dec[i] = rd[n-i-1];
		int maxLen = 1;
		for(int i=0;i<n;i++)
			if(inc[i]>1 && dec[i]>1)
				maxLen = Math.max(maxLen, inc[i] + dec[i] -1);
		return n - maxLen;
	}
}
