        for(int i = 0; i < dp1.size(); ++i){
            if(dp1[i] > 1 && dp2[i] > 1){
                maxToKeep = max(maxToKeep, dp1[i] + dp2[i] - 1);
            }
        }

        return nums.size() - maxToKeep;
