class Solution 
{
public:
//IF WE FIND THE LENGTH OFM THE LONGEST MOUNTAIN ARRAY and SUBTRACT FROM THE LENGTH OF NUMS ARRAY WE GET THE REQUIRED NO. OF REMOVALS.
    int LISbinarySearch(vector<int>& nums,vector<int>& lis)
    {
        if(nums.size()==0)
            return 0;

        vector<int> ans;//this array will store the length of LIS considering each element(index) as pivot(endpoint).
        //current element tak sabse lambe increasing substring ka length kitna he.

        //length of LIS for the first element is always 1. as there are no elements smaller then the first element of nums array.

        lis.push_back(1);
        ans.push_back(nums[0]);

        for(int index=1;index<nums.size();index++)
        {
            if(nums[index]>ans.back())
            {
                ans.push_back(nums[index]); //if the current element of nums array is greater than the last inserted element of ans array then just replace it in the last (index)position.

                lis.push_back(ans.size());
            }
            else
            {
                //justBiggerIndex return the index of the element in ans array which is just greater than the cureent element of nums array.
                int justBiggerIndex=lower_bound(ans.begin(),ans.end(),nums[index])-ans.begin();
                //insert it at that position.
                ans[justBiggerIndex]=nums[index];
                lis.push_back(justBiggerIndex+1);
                //in this case justBiggerIndex+1 will be the length of LIS just less than the current element.
            }
        }
        return ans.size();
    }
    int minimumMountainRemovals(vector<int>& nums) 
    {

        vector<int> lis;
        //this array stores the length of the LIS till each element of nums array (considering it as pivot = last element).(xth(x is the index) element tak LIS ka length kitna he.)
        vector<int> lds; //lds=longest decreasing subsequence.

        LISbinarySearch(nums,lis);
        //this array stores the length of the LIS till each element of reversed nums array.works just like the lis array but for reversed nums array.

        reverse(nums.begin(),nums.end());

        LISbinarySearch(nums,lds);

        //when we reverse the lds array we get the longest decreasing subsequence of the nums array.//considering each element as the pivot.(starting point)
        reverse(lds.begin(),lds.end());


         int maximumLengthOfMountainArray=INT_MIN;

        for(int k=0;k<nums.size();++k)
        {
            if(lis[k]==1 || lds[k]==1)
                continue;
            maximumLengthOfMountainArray=max(maximumLengthOfMountainArray,lis[k]+lds[k]-1); //as one element will be repeated twice in , once in lis array & once in lds array thus we subtract 1.
        }

        int MinimumNumberofRemovals=nums.size()-maximumLengthOfMountainArray; //rest of the element need to be removed.
        return MinimumNumberofRemovals;
    }
};
