            if not piles or x > piles[-1]:
                piles.append(x)
                lis1[i] = len(piles)
                continue
            left = bisect.bisect_left(piles, x)
            lis1[i] = left + 1
            piles[left] = x

        piles.clear()
        for i in range(size-1,-1,-1):
            x = nums[i]
            if not piles or x > piles[-1]:
                piles.append(x)
                lis2[i] = len(piles)
                continue
            left = bisect.bisect_left(piles, x)
            lis2[i] = left + 1
            piles[left] = x

        ans = size + 1
        for i in range(1, size-1):
            if lis1[i] == 1 or lis2[i] == 1: continue
            removed = size - (lis1[i] + lis2[i] - 1)
            ans = ans if ans < removed else removed
        return ans
