class Solution {
public:
    int maxProduct(string s) {
        int n = s.length();
        int p = pow(2,n);
        vector<pair<int,int> > vec;
        for(int i = 1;i < p;i++){
            int a = isPalindrome(s,i);
            if(a != -1)
            vec.push_back({i,a});   
        }
        int ans = 1;
        for(int i = 0;i < vec.size();i++)
        for(int j = i+1;j < vec.size();j++)
        if((vec[i].first & vec[j].first) == 0)
        ans = max(ans,vec[i].second * vec[j].second); 
        return ans;

    }
  int isPalindrome(string& s,int p){
      vector<int> vec;
      for(int i = 0;i < s.length();i++)
      if(p & (1<<i))
      vec.push_back(i);   
      int index = vec.size()-1;
      for(int i = 0;i <= index;i++,index--)
      if(s[vec[i]] != s[vec[index]])
      return -1;
      return vec.size();
   }
};
