        for(int mask = 1; mask < (1 << N); ++mask) {
            string cur;
            for(int i = 0; i < N; ++i)
                if(mask & (1 << i))
                    cur += s[i];
            if(ispalin(cur))
                dp[mask] = cur.size();
            else {
                for(int sub = mask; sub; sub = (sub - 1) & mask)
                    dp[mask] = max(dp[mask], dp[sub]);
            }
        }
        int ans = 0;
        for(int mask = 0; mask < (1 << N); ++mask) {
            int a = dp[mask];
            int other = 0;
            for(int i = 0; i < N; ++i)
                if(!(mask & (1 << i)))
                    other |= 1 << i;
            int b = dp[other];
            ans = max(ans, a * b);
        }
        return ans;
    }
};
