class Solution {
public:
    vector<vector<int>>ar;
    bool check(vector<int>&a,vector<int>&b){       
        for(int i=0;i<a.size();i++)
            for(int j=0;j<b.size();j++)
                if(a[i]==b[j]) return false;
        return true;
