    void calc(string &s,vector<int>&ans,int i){
        if(isPalin(s,ans))
           ar.push_back(ans);

        if(i>=s.length()) return;
        ans.push_back(i);
        calc(s,ans,i+1);
        ans.pop_back();
        calc(s,ans,i+1);
