            int right = fun2(i+1,vis_,vis,s);
            return max(left,right);
        }
        return fun2(i+1,vis_,vis,s);
    }

    int fun1(int i,int vis,string &s){

        int n = s.size();
        if(i == n){

            int len = fun2(0,0,vis,s);

            string tmp;

            for(int i=0;i<s.size();i++){
                int mask = (1 << i);
                if((mask & vis) != 0){
                    tmp.push_back(s[i]);
                }
            }
            if(check(tmp)){
                return (len)*(tmp.size());   
            }
            return 0;
        } 


        int mask = (1 << i);
        int left = fun1(i+1,mask | vis,s);
        int right = fun1(i+1,vis,s);

        return max(left,right);

    }

    int maxProduct(string s) {
        return fun1(0,0,s);

    }

