```
	class Solution {
public:
    vector<vector<int>> dp;
    int LCS(int i,int j,string &s1,string &s2){

        int n1 = s1.size();
        int n2 = s2.size();

        if(i == n1 or j == n2){
            return 0;
        }
        if(dp[i][j] != -1) return dp[i][j];
        if(s1[i] == s2[j]){
            return dp[i][j] = 1 + LCS(i+1,j+1,s1,s2);
        }

        return dp[i][j] = max(LCS(i+1,j,s1,s2),LCS(i,j+1,s1,s2));

    }

    int LPS(string &s){
        string t = s;
        reverse(s.begin(),s.end());
        dp = vector<vector<int>> (s.size(),vector<int> (s.size(),-1));
        return LCS(0,0,s,t);
    }

    int fun1(int i,int vis,string &s){

        int n = s.size();
        if(i == n){
            string s1,s2;
            for(int j=0;j<n;j++){
                int mask = (1 << j);
                if((vis & mask ) == 0){
                    s2.push_back(s[j]);
                }else{
                    s1.push_back(s[j]);
                }
            }        
            return LPS(s1)*LPS(s2);
        } 

        int mask = (1 << i);
        int left = fun1(i+1,mask | vis,s);
        int right = fun1(i+1,vis,s);

        return max(left,right);

    }

    int maxProduct(string s) {
        int n = s.size();
        return fun1(0,0,s);
    }
};

