class Solution {
public:
    vector<vector<int>> dp;

    int LPS_(int i,int j,string &s){

        if(i > j) return 0;
        if(i==j) return 1;
        if(dp[i][j] != -1) return dp[i][j];
        if(s[i] == s[j]){
            return dp[i][j] = 2 + LPS_(i+1,j-1,s);
        }
        return dp[i][j] = max(LPS_(i+1,j,s),LPS_(i,j-1,s));
    }

    int LPS(string &s){
        dp = vector<vector<int>> (s.size(),vector<int> (s.size(),-1));
        return LPS_(0,s.size()-1,s);
    }

    int fun1(int i,int vis,string &s){

        int n = s.size();
        if(i == n){
            string s1,s2;
            for(int j=0;j<n;j++){
                int mask = (1 << j);
                if((vis & mask ) == 0){
                    s2.push_back(s[j]);
                }else{
                    s1.push_back(s[j]);
                }
            }        
            return LPS(s1)*LPS(s2);
        } 

        int mask = (1 << i);
        int left = fun1(i+1,mask | vis,s);
        int right = fun1(i+1,vis,s);

        return max(left,right);

    }

    int maxProduct(string s) {
        int n = s.size();
        return fun1(0,0,s);
    }
};

