#include<bits/stdc++.h>
using namespace std;
int ans;

bool ispal(string &s)
{
    int i = 0 , j = s.length() - 1;
    while(i < j){
        if(s[i] != s[j]) return false;
        ++i;
        --j;
    }
    return true;
}

void solve(int index , string &s , string &s1, string &s2)
{

    /// add this char to s1 or s2 or skip.
    if(ispal(s1) and ispal(s2))
    {
        int len1 = s1.length();
        int len2 = s2.length();
        int len  = len1 * len2;
        ans = max(ans , len);
