    if(index == s.length()) return;
    s1 = s1 + s[index];
    solve(index + 1 , s , s1 , s2);
    s1.pop_back();
    s2 = s2 + s[index];
    solve(index + 1, s , s1 , s2);
    s2.pop_back();
    solve(index + 1 , s , s1 , s2);
}

