class Solution {
public:
    using fun = function<void(int, string&&, string&&)>;
    int maxProduct(string s, int best = 0) {
        auto ok = [](auto& s) {
            int N = s.size(),
                i = 0,
                j = N - 1;
            while (i < j && s[i] == s[j])
                ++i, --j;
            return j <= i;
        };
        fun go = [&](auto i, auto&& A, auto&& B) {
            if (ok(A) && ok(B))
                best = max(best, int(A.size() * B.size()));
            if (i == s.size())
                return;
            A.push_back(s[i]);
            go(i + 1, move(A), move(B));
            A.pop_back();
            B.push_back(s[i]);
            go(i + 1, move(A), move(B));
            B.pop_back();
            go(i + 1, move(A), move(B));
        };
        go(0, {}, {});
        return best;
    }
};
