class Solution {
public:
	int ans = 0;
	int n;
	//function for checking is given string is Palindrome
	bool palindrome(string s)
	{
		int i = 0;
		int j = s.size() - 1;
		while (i <= j)
		{
			if (s[i++] != s[j--])
			{
				return false;
			}
		}
		return true;
	}

	void solve(int index, string &s, string &s1, string &s2)
	{
		//If index >= n (where n = s.size()) then return
		if (index >= n)
		{
			//If both s1 and s2 are palindrome then this is as required in problem
			//so update ans with ans = max(ans, s1.size() * s2.size());
			if (palindrome(s1) && palindrome(s2))
			{
				ans = max(ans, (int)s1.size() * (int)s2.size());
			}
			return;
		}

		//1. include s[index]
			//1.(i) adding s[index] in string s1
			s1.push_back(s[index]);
			solve(index + 1, s, s1, s2);
			s1.pop_back();
			//1.(ii) adding s[index] in string s2
			s2.push_back(s[index]);
			solve(index + 1, s, s1, s2);
			s2.pop_back();

		//2. not including s[index] in string generation
		solve(index + 1, s, s1, s2);
	}

	int maxProduct(string s) {
		int index = 0;
		n = s.size();
		string s1 = "";
		string s2 = "";
		solve(index, s, s1, s2);
		return ans;
	}
};
