class Solution {
public:
    bool isPalindrome(string str)
    {
        // Start from leftmost and rightmost corners of str
        int l = 0;
        int h = str.size()-1;

        // Keep comparing characters while they are same
        while (h > l)
        {
            if (str[l++] != str[h--])
            {
                return false;
            }
        }
        return true;
    }
    int maxProduct(string s) {
        vector<pair<string,long long>> v;

        long long n = s.size();
        long long power = pow(2,n);


        for(int i=0;i<power;i++){
            string ans="";
            for(int j=0;j<n;j++){
                if(i&1<<j){
                    ans = ans + s[j];
                }
            }
            if(isPalindrome(ans)){
                v.push_back({ans,i});
            }
        }
        long long val=1;
        for(int i=0;i<v.size()-1;i++){
            for(int j=1;j<v.size();j++){
                if((v[i].second & v[j].second)==0){

                        long long ik = v[i].first.size();
                        long long jk = v[j].first.size();

                        if(ik*jk>val){
                            val = ik*jk;
                        }

                }
            }
        }
        return val;
    }
};
