    void solve(int i,string &s1,string &s2,string &s,int &ans){
        //base case
        if(i>=s.size()){
            if(isPalindrome(s1) && isPalindrome(s2)){
                int prod=s1.size()*s2.size();
                ans= max(ans,prod);
            }
            return;
        }
        //dont pick
        solve(i+1,s1,s2,s,ans);

        //pick
        s1+=s[i];
        solve(i+1,s1,s2,s,ans);
        s1.pop_back();
        s2+=s[i];
        solve(i+1,s1,s2,s,ans);
        s2.pop_back();
    }

    int maxProduct(string s) {
        int n=s.size();
        int ans=0;
        string s1="",s2="";
        solve(0,s1,s2,s,ans);
        return ans;
    }
};
