class Solution {
public:
    long long res;
    bool ispal(string &s)
    {
        int start=0,end=s.size()-1;
        while(start<end)
        {
            if(s[start]!=s[end])
                return false;
            start++;
            end--;
        }
        return true;
    }
    void backtrack(string s,int idx,string &s1,string &s2)
    {
        if(idx>=s.size())
        {
            if(ispal(s1)&&ispal(s2))
            {
                long long val=s1.size()*s2.size();
                res=max(val,res);
            }
            return ;
        }
        s1.push_back(s[idx]);
        backtrack(s,idx+1,s1,s2);
        s1.pop_back();

        s2.push_back(s[idx]);
        backtrack(s,idx+1,s1,s2);
        s2.pop_back();

        backtrack(s,idx+1,s1,s2);
    }
    int maxProduct(string s)
    {
        string s1="",s2="";
        backtrack(s,0,s1,s2);
        return res;

    }
};
