class Solution {
public:
    bool check_palindrom(int m,string s){
        int i=0;
        string temp="";
        while(m>0){
            if(m&1){
                temp+=s[i];
            }
            m=m>>1;
            i++;
        }
        int start=0;
        int end=temp.length()-1;
        while(start<=end){
            if(temp[start]!=temp[end])return false;
            start++;
            end--;
        }
        return true;
    }
    int find_ans(int m,string s){
        int n=s.length();
        string temp="";
        for(int i=0;i<n;i++){
            if((m&(1<<i))==0){
                temp+=s[i];
            }
        }
        int l=temp.length();
        if(l==0)return 0;
        vector<vector<int>>dp(l,vector<int>(l,0));
        for(int i=0;i<l;i++)dp[i][i]=1;
        for(int len=2;len<=l;len++){
            for(int i=0;i+len-1<l;i++){
                int j=i+len-1;
                if(len==2){
                    if(temp[i]==temp[j])dp[i][j]=2;
                    else dp[i][j]=1;
                }
                else{
                    if(temp[i]==temp[j])dp[i][j]=2+dp[i+1][j-1];
                    else dp[i][j]=max(dp[i+1][j],dp[i][j-1]);
                }
            }
        }
        return dp[0][l-1];

    }
    int maxProduct(string s) {
