class Solution {
public:
    int LPS(string& s1) {
        string s2 = s1;
        reverse(s2.begin(), s2.end());

        int n = s1.size();
        int dp[n + 1][n + 1];
        for(int i = 0; i < n; i++) dp[i][0] = 0, dp[0][i] = 0;

        for(int i = 1; i <= n; i++){
            for(int j = 1; j <= n; j++){
                if(s1[i - 1] == s2[j - 1]){
                    dp[i][j] = 1 + dp[i - 1][j - 1];
                }
                else dp[i][j] = max(dp[i - 1][j],dp[i][j - 1]);
            }
        }
        return dp[n][n];
    }

    int maxProduct(string s) {
        int ans = 0, n = s.size();
        for(int mask = 1; mask < (1 << n) - 1; mask++){
            string a = "", b = "";
            for(int i = 0; i < n; i++){
                if(mask & (1 << i)) a += s[i];
                else b += s[i];
            }
            ans = max(ans, LPS(a) * LPS(b));
        }
        return ans;
    }
};
